(function() { tinymce.PluginManager.requireLangPack("code"); tinymce.create("tinymce.plugins.codePlugin", { init: function(a, b) { a.addCommand("mcecode", function() { a.windowManager.open({ file: b + "/dialog.htm", width: 320 + parseInt(a.getLang("code.delta_width", 0)), height: 120 + parseInt(a.getLang("code.delta_height", 0)), inline: 1 }, { plugin_url: b, some_custom_arg: "custom arg" }) }); a.addButton("code", { title: "code.desc", cmd: "mcecode", image: b + "/img/example.gif" }); a.onNodeChange.add(function(d, c, e) { c.setActive("code", e.nodeName == "IMG") }) }, createControl: function(b, a) { return null }, getInfo: function() { return { longname: "code plugin", author: "Some author", authorurl: "http://tinymce.moxiecode.com", infourl: "http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/example", version: "1.0"} } }); tinymce.PluginManager.add("code", tinymce.plugins.codePlugin) })();